import React from 'react';
import { Link } from 'react-router-dom';

export default function Home() {
  return (
    <div className="flex flex-col items-center justify-center py-20">
      <div className="flex items-center mb-6 space-x-4">
        {}
        <img
          src="/health.png"
          alt="HealthQuest Logo"
          className="w-20 h-20"
          style={{ objectFit: 'contain' }}
        />
        <h1 className="text-5xl font-bold text-blue-700">HealthQuest</h1>
      </div>
      <p className="text-xl mb-8 text-gray-700">Your personalized health and fitness assistant.</p>
      <Link to="/signup" className="bg-blue-600 text-white px-6 py-3 rounded-lg text-xl font-semibold shadow hover:bg-blue-700">
        Get Started
      </Link>

      <div className="mt-16 grid grid-cols-1 md:grid-cols-3 gap-8">
        <Feature
          title="Fitness Tracking"
          desc="Track your workouts, see progress, and schedule routines easily."
          to="/dashboard"
        />
        <Feature
          title="Personalized Recommendations"
          desc="Get custom workouts and nutrition advice based on your goals."
          to="/workouts"
        />
        <Feature
          title="Nutrition & Hydration"
          desc="Log meals, monitor nutrients, and track your daily water intake."
          to="/nutrition"
        />
      </div>
    </div>
  );
}

function Feature({ title, desc, to }) {
  return (
    <Link to={to} className="p-6 bg-white shadow rounded-lg text-center hover:shadow-lg transition cursor-pointer block">
      <h3 className="text-xl font-bold mb-2 text-blue-600">{title}</h3>
      <p className="text-gray-600">{desc}</p>
    </Link>
  );
}
import Footer from '../components/Footer';

