import React from 'react';
import { useNavigate } from 'react-router-dom';

export default function HomePage() {
  const navigate = useNavigate();

  const features = [
    {
      title: "Fitness Tracking",
      description: "Track your workouts, see progress, and schedule routines easily.",
      route: "/fitness",
    },
    {
      title: "Personalized Recommendations",
      description: "Get custom workouts and nutrition advice based on your goals.",
      route: "/recommendations",
    },
    {
      title: "Nutrition & Hydration",
      description: "Log meals, monitor nutrients, and track your daily water intake.",
      route: "/nutrition",
    },
  ];

  return (
    <div className="grid grid-cols-1 md:grid-cols-3 gap-6 p-6">
      {features.map((feature, index) => (
        <div
          key={index}
          className="p-4 bg-white rounded-2xl shadow hover:shadow-lg cursor-pointer transition"
          onClick={() => navigate(feature.route)}
        >
          <h3 className="text-xl font-bold mb-2">{feature.title}</h3>
          <p className="text-gray-600">{feature.description}</p>
        </div>
      ))}
    </div>
  );
}
