import React from 'react';
import { Link } from 'react-router-dom';
import Footer from '../components/Footer';

export default function Home() {
  return (
    <div className="flex flex-col min-h-screen bg-gray-50">
      <main className="flex flex-col items-center justify-center flex-1 py-32">
        <div className="flex flex-col items-center mb-10">
          {/* Logo (optional) */}
          <img
            src="/health.png"
            alt="HealthQuest Logo"
            className="w-24 h-24 mb-4"
            style={{ objectFit: 'contain' }}
          />
          {/* Title */}
          <h1 className="text-7xl font-extrabold text-blue-700 mb- text-center">
            HealthQuest
          </h1>
          {/* Subtitle */}
          <p className="text-3xl mb-9 text-gray-700 font-medium text-center">
            Your personalized health and fitness assistant.
          </p>
          {/* Get Started Button */}
          <Link
            to="/signup"
            className="bg-blue-600 text-white px-8 py-4 rounded-lg text-2xl font-semibold shadow hover:bg-blue-700 transition mb-8"
          >
            Get Started
          </Link>
        </div>
        {/* Features */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-8 w-full max-w-4xl">
          <Feature
            title="Fitness Tracking"
            desc="Track your workouts, see progress, and schedule routines easily."
            to="/dashboard"
          />
          <Feature
            title="Personalized Recommendations"
            desc="Get custom workouts and nutrition advice based on your goals."
            to="/workouts"
          />
          <Feature
            title="Nutrition & Hydration"
            desc="Log meals, monitor nutrients, and track your daily water intake."
            to="/nutrition"
          />
        </div>
      </main>
      <Footer />
    </div>
  );
}

function Feature({ title, desc, to }) {
  return (
    <Link to={to} className="p-8 bg-white shadow rounded-lg text-center hover:shadow-lg transition cursor-pointer block">
      <h3 className="text-2xl font-bold mb-3 text-blue-600">{title}</h3>
      <p className="text-gray-600 text-base">{desc}</p>
    </Link>
  );
}
